mapper.update:
update role
set role_code = #{roleCode},
    role_name = #{roleName},
    role_desc = #{roleDesc},
    role_status = #{roleStatus}
where id = #{id}

mapper.select:
select id, role_code, role_name, role_desc, role_status
from role
where id = #{id}

select id, role_code, role_name, role_desc, role_status
from role
where role_name = #{roleName}
